const Product = (props) => {
    const onClick = () => {
      props.handleAddProduct({name: props.name, price: props.price, id: props.id, image: props.image});
    }
  
    return (
      <div className='product' onClick={onClick}>
        <img src={props.image} alt={props.name} />
        <p>{props.name}</p>
        <p>${props.price}</p>
      </div>
    );
  };
  
  export default Product;
