import{ useState } from 'react';
import { login, saveUser } from "../api/api.js";
import { useNavigate } from 'react-router-dom';

function Login (){
    // const userRef = useRef();
    // const errRef = useRef();

    // const [user, setUser] = useState('');
    // const [pwd, setPwd] = useState('');
    // const [errMsg, setErrMsg] = useState('');
    // const [success, setSuccess] = useState(false);

    // useEffect(() => {
    //     userRef.current.focus();
    // }, [])

    // useEffect(() => {
    //     setErrMsg('');
    // }, [user, pwd])

    
    const navigate = useNavigate();
    const [values, setValues] = useState({
        email: "",
        password: "",
    });

    const handleSubmit = async (e) =>{
        e.preventDefault();
        try{
            const response = await login(values);
            saveUser(response.data);
            navigate("/waiter");
        } catch {
            if(!err?.response){
                console.log("No hay respues del server")
            }else if(err.response?.status === 400){
                console.log("El usuario o contrase単a son erroneos")
            }
        } 
        // navigate('/order'); 
    }

    const handleChange = (e) => {
        const { target } = e;
        const { name, value } = target;
        const newValues = {
          ...values,
          [name]: value,
        };
        setValues(newValues);
      }

    return (
       <form onSubmit={handleSubmit}>
           <label htmlFor="email">Email:</label>
           <input type="email"
                name='email'
                placeholder='User'
                className='email'
                value={values.email}
                required
                onChange={handleChange}
            />
            <label htmlFor="pws">Contrase単a:</label>
            <input
                type="password"
                name="password"
                placeholder="Contrase単a"
                className="password"
                value={values.password}
                required
                onChange={handleChange}
            />
            <select className="position">
                <option value="select">Cargo</option>
                <option value="admin">Administrador</option>
                <option value="waiter">Mesero</option>
                <option value="chef">Chef</option>
            </select>
        <button type="submit" className="btn-login">Enviar</button>
       </form>
    )
}
        
export default Login;
