import React from "react";
import { BrowserRouter as Router, Route, Routes, useNavigate  } from 'react-router-dom';

import Home from "../pages/Home";
import Waiter from "../pages/Waiter";
import Kitchen from "../pages/Kitchen";
import Admin from "../pages/Admin";
import NotFound from "../pages/notFound";
import { getLoggedUser } from "../api/api";

const router = () => {

  return (
    <Router>
        <Routes>

          <Route path='/' element={<Home />}/>

          <Route path='/admin' element={
            <PrivateRoute>
              <Admin />
            </PrivateRoute>
          }/>

          <Route path='/waiter' element={
            <PrivateRoute>
              <Waiter />
            </PrivateRoute>
          }/>

          <Route path='/kitchen' element={
            <PrivateRoute>
              <Kitchen />
            </PrivateRoute>
          }/>

          <Route path='/404' element={<NotFound/>} />

        </Routes>
    </Router>
  );
}

const PrivateRoute = ({ children }) => {
  const authed = getLoggedUser(children)
  console.log(authed.user.roles);
  
    if(authed.user.roles === true){
      return <useNavigate to='waiter' />
    }else if(authed.user.roles === "admin"){
      return <useNavigate to="/admin" />
    }else if(authed.user.roles === "chef"){
      return <useNavigate to="/kitchen" />
    }

    return <useNavigate to="/404" />
    //authed.user.roles ? children : <Navigate to="/404" />
}

export default router;
