import React, {useState, useEffect, useMemo} from "react";
import createUser from "../api/api"

function CreateUsers({edit, editUser, success,  userData}) {
    const channel = useMemo(() => new BroadcastChannel("user"), []);
    
    const [hasError, setHasError] = useState("");
    const [values, setValues] = useState(userData || {
    // estado para guardar los datos del formgitulario
        name: "",
        email: "",
        password: "",
        roles: "",
    });
    const [message, setMessage] = useState("");

    const user = {
        name: values.name,
        email: values.email,
        password: values.password,
        roles: values.roles,
    };

  const onClickUpdate = () => {
    editUser(values).then(() => {
      success();
    })
  }

  const startRegister = async (e) => {
    e.preventDefault();
    // funcion para iniciar sesion, (se llama con el handleSumbmit)
    try {
      await createUser(user).then((response) => {
        channel.postMessage("registerUser");
        setMessage("Usuario creado correctamente");
      }); // llamada a la funcion login de la api
    } catch {
      setHasError("El usuario ya está registrado");
    }
    setTimeout(() => {
      setMessage(null);
    }, 1500);
    setValues({
      name: "",
      email: "",
      password: "",
      roles: "",
    });
  };

  useEffect(() => {
    return () => channel.close();
  }, [channel]);

  const handleChange = (e) => {
    // funcion para guardar los datos del formulario
    const { target } = e;
    const { name, value } = target;

    const newValues = {
      // nuevo estado con los datos del formulario
      ...values, // estado anterior
      [name]: value, // nuevo valor
    };
    setValues(newValues); // actualizar el estado
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

	return (
			<div>
                <form action="" onSubmit={handleSubmit}>
                    <div>
                        <input type="text" id="name" name="name" placeholder="Name" value={values.name} onChange={handleSubmit} />
                        <input type="email" id="email" name="email" placeholder="email" value={values.email} onChange={handleSubmit} />
                        <input type="password" id="password" name="password" placeholder="password" value={values.password} onChange={handleSubmit} />
                        <input type="name" id="roles" name="roles" placeholder="roles" value={values.roles} onChange={handleSubmit} />
                        {edit ? (<button type="submit" className="btn-register" onClick={onClickUpdate}>
                            GUARDAR
                            </button>) : (<button type="submit" className="btn-register" onClick={startRegister}>
                            REGISTRAR
                            </button>)}
                            {hasError && (
                            <Alert data-testid="register-error-message">{hasError}</Alert>
                        )}
                    </div>
                </form>
                {message && (
                    <p>{message}</p>
                )}

			</div>
	);
}

export default CreateUsers;
