import React, { useContext } from 'react';
import CartContext from '../Context/CartContext';
import deleteIcon from '../assets/delete.png';

const ItemCart = ({item}) => {
    const {deleteItemToCart, addItemToCart} = useContext(CartContext);

    return (
            <tr>
                <th scope="row">{item.name}</th>
                <th scope="row">{item.amount}</th>
                <th scope="row">${item.price*item.amount}</th>
                <th scope="row"><button className="btn btn-sm btn-primary" onClick={()=> {addItemToCart(item)}}><i src={deleteIcon}>ADD</i></button>
                <button class="btn btn-secondary btn-sm"  onClick={()=> {deleteItemToCart(item)}}>DELETE</button></th>
            </tr>
    );
}

export default ItemCart;
