import React from 'react';
import "../components/stylesheets/Cards.css";
import { products } from '../api/Products';
import { useEffect, useState } from "react";

function Cards({
	image, name, complement, price, onClick
}) {
	const [productos, setProductos] = useState([]);

  useEffect(() => {
    products()
    .then((response) =>{
      console.log(response.data);
      setProductos(response.data)
    })
    .catch(() =>{});
  }, []);

	return (
		<div>
			{productos.map((producto)=>(
				<div className='each-card'>
				<ul className='items'>
					<div className='image-item'>
						<img src={producto.image} alt='Item'></img>
					</div>
					<li className='item-name'>{name}</li>
					<section className='add-item'>
					<li className='complement' name='complemento'>Complemento: {`${complement !== null ? complement : '' }`}</li>
					</section>
					<li>Precio: ${price}</li>
				</ul>
				<button className='add-btn' onClick={onClick}>Adicionar</button>
			</div>
			))}
		</div>
	);
}

export default Cards;
