    const navigate = useNavigate()
    const [values, setValues] = useState({
        email: "",
        password: "",
      });

    const handleSubmit = async (e) =>{
        e.preventDefault();
        
        try{
            const response = await login(value);
            saveUser(response.data);
            navigate("/waiter")
        } catch {
            console.log("error")
        } 
        // navigate('/order'); 
    }

    const handleChange = (e) => {
        const { target } = e;
        const { name, value } = target;
        console.log(value);
        const newValues = {
          ...values,
          [name]: value,
        };
        setValues(newValues);
      }

    return (
       <form onSubmit={handleSubmit}>
           <label htmlFor="email">Email:</label>
           <input type="email"
           name='email'
           placeholder='User'
           className='email'
           value={values.email}
           required
           onChange={handleChange}
            />
            <label htmlFor="pws">Contrase√±a:</label>
           <input type="pws"
           name='pws'
           placeholder='pws'
           className='pws'
           value={values.password}
           required
           onChange={handleChange}
            />
       </form>
    )
}
        
export default Login;
