import React from "react";
import { BrowserRouter as Router, Route, Routes, Navigate  } from 'react-router-dom';

import Home from "../pages/Home";
import Waiter from "../pages/Waiter";
import Kitchen from "../pages/Kitchen";
import Admin from "../pages/Admin";
import NotFound from "../pages/notFound";
import { getLoggedUser } from "../api/api";

const router = () => {

  const dataUser = getLoggedUser();
  //const token = getToken();

 console.log(dataUser?.user);

  return (
    <Router>
        <Routes>

          <Route path='/' element={<Home />}/>

          <Route path='/admin' element={
            <PrivateRoute>
              <Admin />
            </PrivateRoute>
          }/>

          <Route path='/waiter' element={
            <PrivateRoute>
              <Waiter />
            </PrivateRoute>
          }/>

          <Route path='/kitchen' element={
            <PrivateRoute>
              <Kitchen />
            </PrivateRoute>
          }/>

          <Route path='/404' element={<NotFound/>} />

        </Routes>
    </Router>
  );
}

function PrivateRoute({ children }) {
  // useAuth es un hook personalizado para obtener el estado de autenticación del usuario actual
  const auth = getLoggedUser();

  return (
    auth ? children : <Navigate to="/" />
  );
}

export default router;
