    const { id } = item;

    return (
        <div>
            <div>
                <p>{item.name}</p>
                <p>{item.amount} ${item.price*item.amount}</p>
                <div>
                    <button onClick={()=> {addItemToCart(item)}}>ADD</button>
                    <button onClick={()=> {deleteItemToCart(item)}}>DELETE</button>
                </div>
            </div>
                <button>SEND ORDER</button>
        </div>
    );
}

export default ItemCart;
