import { useState, useEffect, useMemo } from "react";
import { createUser } from "../api/api";

const CreateUsers = () => {
  const [email] = useState('');
  const [password] = useState('');

  const [values, setValues] = useState({
      email: "",
      password: "",
  });

  const [errMsg, setErrMsg] = useState('');

  useEffect(() => {
    sessionStorage.clear();
    setErrMsg('');
}, [email, password])

const handleSubmit = async (e) =>{
  e.preventDefault();
  try{
      const response = await login(values);
      const { user } = response.data;
      saveUser(response.data);
          if (user.roles.waiter) {
              navigate('/waiter');
          } else if (user.roles.chef) {
              navigate('/kitchen');
          } else if (user.roles.admin) {
              navigate('/admin')
          }
  } catch(err) {
      if(!err?.response){
          setErrMsg("There is no response from the server")
      }else if(err.response?.status === 400){
          setErrMsg("The email or password is wrong")
      }else if(err.response?.status === 401){
          setErrMsg("Without authorization")
      }else{
          setErrMsg("Fallo al ingresar")
      }
  }
}

const handleChange = (e) => {
  const newValues = {
  ...values,
  [e.target.name]: e.target.value,
  };
  setValues(newValues);
}

  return (
    <div>
      <form className="form-workers" onSubmit={handleSubmit}>
        <div>
        <p>name</p>
          <input
            id="name"
            type="name"
            name="name"
            placeholder="Nombre"
            className="name-worker"
            value={values.name}
            onChange={handleChange} // cuando se cambia el valor del input
            data-testid="name-worker"
          />
        </div>
        <div>
            <p>Email</p>
          <input
            id="email"
            type="email"
            name="email"
            placeholder="Correo Electrónico"
            className="email-worker"
            value={values.email}
            onChange={handleChange} // cuando se cambia el valor del input
            data-testid="email-worker"
          />
        </div>
        <div>
        <p>password</p>
          <input
            id="password" // input para el password
            type="password"
            name="password"
            placeholder="Contraseña"
            className="password-worker"
            value={values.password}
            onChange={handleChange} // cuando se cambia el valor del input
            data-testid="password-worker"
          />
            {/* <span>{errors.password.message}</span> */}
        </div>

        <div>
            roles
          <input
            id="roles" // input para el password
            type="name"
            name="roles"
            placeholder="Rol"
            className="roles-worker"
            value={values.roles}
            onChange={handleChange} // cuando se cambia el valor del input
            data-testid="roles-worker"
          />
        </div>
          <button type="submit" className="btn-register" onClick={startRegister}>REGISTRAR</button>
        {hasError && (
            <p>{hasError}</p>
        )}
      </form>
      {message && (
        <p>{message}</p>
      )}
    </div>
  );
};

export default CreateUsers;
