import { useState, useEffect } from "react";
import { createUser } from "../api/api";

const CreateUsers = () => {
  const [email] = useState('');
  const [password] = useState('');

  const [values, setValues] = useState({
      name: "",
      email: "",
      password: "",
      roles: ""
  });

  const [errMsg, setErrMsg] = useState('');

  useEffect(() => {
    sessionStorage.clear();
    setErrMsg('');
}, [email, password])

const handleSubmit = async (e) =>{
  e.preventDefault();
  try{
      const response = await createUser(values);
      const { user } = response.data;
      createUser(user)
  } catch(err) {
          setErrMsg(err)
  }
}

const handleChange = (e) => {
  const newValues = {
  ...values,
  [e.target.name]: e.target.value,
  };
  setValues(newValues);
}

  return (
    <div>
      <form className="form-workers" onSubmit={handleSubmit}>
        <div>
        <p>name</p>
          <input
            id="name"
            type="name"
            name="name"
            placeholder="Nombre"
            className="name-worker"
            value={values.name}
            onChange={handleChange} // cuando se cambia el valor del input
            data-testid="name-worker"
          />
        </div>
        <div>
            <p>Email</p>
          <input
            id="email"
            type="email"
            name="email"
            placeholder="Correo Electrónico"
            className="email-worker"
            value={values.email}
            onChange={handleChange} // cuando se cambia el valor del input
            data-testid="email-worker"
          />
        </div>
        <div>
        <p>password</p>
          <input
            id="password" // input para el password
            type="password"
            name="password"
            placeholder="Contraseña"
            className="password-worker"
            value={values.password}
            onChange={handleChange} // cuando se cambia el valor del input
            data-testid="password-worker"
          />
            {/* <span>{errors.password.message}</span> */}
        </div>

        <div>
            roles
          <select
            id="roles" // input para el password
            name="roles"
            placeholder="Rol"
            className="roles-worker"
            value={values.roles}
            onChange={handleChange} // cuando se cambia el valor del input
            data-testid="roles-worker"
          >
          <option value="0">Rol</option>
          <option value="admin">Administrator</option>
          <option value="chef">Chef</option>
          <option value="waiter">Waiter</option>
        </select>
        </div>
          <button type="submit" className="btn-register" onClick={handleSubmit}>REGISTRAR</button>
        {errMsg && (
            <p>{errMsg}</p>
        )}
      </form>
    </div>
  );
};

export default CreateUsers;
