import { getOrder } from '../api/Products';
import { Link } from 'react-router-dom';
import styles from '../components/stylesheets/Waiter.module.css';

export default function Kitchen() {

  const [orders, setOrders] = useState([]);

  useEffect(() => {
    getOrder()
    .then((res) => {
      console.log(res.data)
      setOrders(res.data)
    })
    .catch()
  }, [])

  return (
    <div className={styles.Bigbox}>
      <h1>PÃ¡gina de cocina</h1>
      <p>
        <Link to="/">Sign Out</Link>
      </p>
      {orders.map((order)=>(
        <div key={order.id}>
          <h2>ORDER</h2>
         <ul className={styles.Li}>
            <p>{order.client}</p>
            <p>{order.status}</p>
            <p> Created: {order.dataEntry}</p>
            {order.dateProcessed &&  <p className={styles.delivered}> Delivered: {order.dateProcessed}</p>}
            <p>{order.products.qty}</p>
             {/* {order.map((orders)=>(
               <li>{orders.products.product.price}</li>
             ))} */}
            {/* <li>{order.products.product.name}</li>
            <li>{order.products.product.price}</li>
            <li>{order.products.qty}</li>
            <li>{order.products.product.qty}</li> */}
          </ul>

        </div>
      ))}
    </div>
  );
}
